//import { Link } from 'react-router-dom'
import 'bootstrap/dist/css/bootstrap.min.css'
import {CDBSidebar, CDBSidebarContent, CDBSidebarMenu, CDBSidebarMenuItem,CDBSidebarFooter,CDBSidebarHeader } from 'cdbreact'
import { Link } from 'react-router-dom'
import React, { useState } from "react";



function SideNav() {
    const [item1, setItem1] = useState("1");

  const toggle = (tab) => {
    if (item1 !== tab) {
      setItem1(tab)
    }
  };
    return(
        <div
      style={{ display: 'flex', height: '100vh', overflow: 'scroll initial' }}
    >
      <CDBSidebar textColor="#fff" backgroundColor="#355173">
        <CDBSidebarHeader prefix={<i className="fa fa-bars fa-large"></i>}>
          <a
          icon="user"
            href="/"
            className="text-decoration-none"
            style={{ color: 'inherit' }}
          >
            Sidebar
          </a>
        </CDBSidebarHeader>

        <CDBSidebarContent className="sidebar-content">
          <CDBSidebarMenu>
            <Link exact to="/dashboard" activeClassName="activeClicked">
              <CDBSidebarMenuItem  icon="columns">Dashboard</CDBSidebarMenuItem>
            </Link>
            <Link exact to="/wallet" activeClassName="activeClicked">
              <CDBSidebarMenuItem icon="wallet" >Wallet</CDBSidebarMenuItem>
            </Link>
          </CDBSidebarMenu>
        </CDBSidebarContent>

        <CDBSidebarFooter style={{ textAlign: 'center' }}>
          <div
            style={{
              padding: '20px 5px',
            }}
          >
            Sidebar Footer
          </div>
        </CDBSidebarFooter>
      </CDBSidebar>
    </div>
    );
}

export default SideNav;
