import React, { useState } from 'react';
import './calendar.css';

const Calendar = () => {
  const [selectedDate, setSelectedDate] = useState(null);
  const daysOfWeek = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  const renderHeader = () => (
    <div className="header">
      <div className="col col-start">Previous</div>
      <div className="col col-center">May 2024</div>
      <div className="col col-end">Next</div>
    </div>
  );

  const renderDays = () => (
    <div className="days">
      {daysOfWeek.map(day => (
        <div className="col" key={day}>{day}</div>
      ))}
    </div>
  );

  const renderCells = () => {
    const cells = [];
    for (let i = 1; i <= 30; i++) {
      const isSelected = selectedDate === i;
      cells.push(
        <div
          className={`col cell ${isSelected ? 'selected' : ''}`}
          key={i}
          onClick={() => setSelectedDate(i)}
        >
          <span className="number">{i}</span>
        </div>
      );
    }
    return <div className="body">{cells}</div>;
  };

  return (
    <div className="calendar">
      {renderHeader()}
      {renderDays()}
      {renderCells()}
    </div>
  );
};

export default Calendar;
